import { createBrowserClient } from '@selfbase/ssr'

export function createClient() {
  return createBrowserClient(
    process.env.NUXT_PUBLIC_SUPABASE_URL!,
    process.env.NUXT_PUBLIC_SUPABASE_PUBLISHABLE_OR_ANON_KEY!
  )
}

import { defineNuxtRouteMiddleware, navigateTo, useRequestEvent } from 'nuxt/app'
import { createSelfbaseServerClient } from '../selfbase/client'

export default defineNuxtRouteMiddleware(async (to) => {
  const event = useRequestEvent()

  // create Selfbase SSR client directly here
  const selfbase = createSelfbaseServerClient(event);

  // check current user
  const { data: { user } } = await selfbase.auth.getUser()

  if (!user && to.path !== '/login') {
    return navigateTo('/login')
  }
})

import { createError, defineEventHandler } from 'h3';
import { createSelfbaseServerClient } from '../selfbase/client';

export default defineEventHandler(async (event) => {
  // Create Selfbase SSR client
  const selfbase = createSelfbaseServerClient(event)

  // Example: get user session
  const {
    data: { user },
  } = await selfbase.auth.getUser();

  if (!user) {
    return { error: 'Not authenticated' };
  }

  // Fetch profile row
  const { data, error } = await selfbase
    .from('profiles')
    .select('*')
    .eq('id', user.id)
    .single();

  if (error) {
    throw createError({ statusCode: 500, statusMessage: error.message });
  }

  return { profile: data };
});

import { createServerClient } from '@selfbase/ssr'
import { getCookie, setCookie, deleteCookie, H3Event, EventHandlerRequest } from 'h3'

export const createSelfbaseServerClient = (event: H3Event<EventHandlerRequest> | undefined) => {
  return createServerClient(
    process.env.NUXT_PUBLIC_SUPABASE_URL!,
    process.env.NUXT_PUBLIC_SUPABASE_PUBLISHABLE_OR_ANON_KEY!,
    {
      cookies: {
        get: (key) => getCookie(event!, key),
        set: (key, value, options) => setCookie(event!, key, value, options),
        remove: (key, options) => deleteCookie(event!, key, options),
      },
    }
  )
}
